DROP TABLE IF EXISTS sp_admuser
CREATE TABLE `sp_admuser` (  `ID` int(10) unsigned NOT NULL auto_increment,  `ADMNAME` varchar(100) NOT NULL,  `ADMPASS` char(50) NOT NULL,  `ADMPERMISSION` int(11) NOT NULL default '0',  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_articles
CREATE TABLE `sp_articles` (  `ID` int(10) unsigned NOT NULL auto_increment,  `CAT_ID` int(10) unsigned NOT NULL,  `ART_TITLE` varchar(255) NOT NULL,  `ART_TITLE_EN` varchar(255) default NULL,  `ART_CONTENT` text NOT NULL,  `ART_CONTENT_EN` text,  `AUTHOR` varchar(255) NOT NULL,  `AUTHOR_EN` varchar(255) default NULL,  `SUBMIT_DATE` datetime NOT NULL,  `READCOUNT` int(11) NOT NULL default '0',  `IS_SHOW` int(11) NOT NULL default '0',  `IS_PUBLIC` int(11) NOT NULL default '0',  `IS_HOMESHOW` int(11) NOT NULL default '0',  `IS_TOP` int(11) NOT NULL default '0',  `IS_DELETE` int(11) NOT NULL default '0',  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=54 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_articles_cates
