DROP TABLE IF EXISTS sp_banner
CREATE TABLE `sp_banner` (  `ID` int(10) unsigned NOT NULL auto_increment,  `IMG_URL` varchar(255) NOT NULL,  `IMG_LINK` varchar(255) default NULL,  `IMG_TITLE` varchar(255) default NULL,  `IMG_DESC` varchar(255) default NULL,  `ORDERNUM` int(10) unsigned NOT NULL,  PRIMARY KEY  (`ID`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_config
CREATE TABLE `sp_config` (  `ID` int(10) unsigned NOT NULL auto_increment,  `SITENAME` varchar(200) NOT NULL,  `SITENAME_EN` varchar(200) default NULL,  `SITE_TITLE` varchar(200) NOT NULL,  `SITE_TITLE_EN` varchar(200) default NULL,  `SITE_DESCRIPTION` text NOT NULL,  `SITE_DESCRIPTION_EN` text,  `SITE_KEYWORDS` text NOT NULL,  `SITE_KEYWORDS_EN` text,  `EMAIL` varchar(255) NOT NULL,  `PHONE` varchar(100) NOT NULL,  `FOX` varchar(100) default NULL,  `QQ` varchar(100) default NULL,  `MSN` varchar(100) default NULL,  `ADDRESS` varchar(255) default NULL,  `ADDRESS_EN` varchar(255) default NULL,  `LINKMAN` varchar(100) default NULL,  `LINKMAN_EN` varchar(100) default NULL,  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_guest_param
CREATE TABLE `sp_guest_param` (  `IS_MAIL` int(11) NOT NULL default '1',  `IS_SHOW` int(11) NOT NULL default '0',  `IS_VERICODE` int(11) NOT NULL default '0',  `RECEIVE_EMAIL` varchar(255) default NULL,  `REPLY_SMTP` varchar(255) default NULL,  `REPLY_SMTP_USERNAME` varchar(255) default NULL,  `REPLY_SMTP_PASSWORD` varchar(255) default NULL) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_guestbook
CREATE TABLE `sp_guestbook` (  `ID` int(10) unsigned NOT NULL auto_increment,  `USER_NAME` varchar(255) NOT NULL,  `EMAIL` varchar(255) NOT NULL,  `QQMSN` varchar(255) default NULL,  `PHONE` varchar(255) default NULL,  `MOBILE` varchar(255) default NULL,  `ADDRESS` varchar(255) default NULL,  `COMPANY` varchar(255) default NULL,  `CON_TITLE` varchar(255) default NULL,  `CONTENT` text NOT NULL,  `CONTENT_REPLY` text,  `IS_MAIL` int(11) NOT NULL default '1',  `SUBMIT_DATE` datetime NOT NULL,  `REPLY_DATE` datetime NOT NULL,  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_images
CREATE TABLE `sp_images` (  `ID` int(10) unsigned NOT NULL auto_increment,  `CAT_ID` int(10) unsigned NOT NULL,  `IMG_NAME` varchar(255) NOT NULL,  `IMG_NAME_EN` varchar(255) default NULL,  `IMG_URL` varchar(255) NOT NULL,  `IMG_DESC` text,  `IMG_DESC_EN` text,  `IS_SHOW` int(11) NOT NULL default '0',  `IS_HOMESHOW` int(11) NOT NULL default '0',  `IS_TOP` int(11) NOT NULL default '0',  `IS_DELETE` int(11) NOT NULL default '0',  `READCOUNT` int(11) NOT NULL default '0',  `SUBMIT_DATE` datetime NOT NULL,  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_images_cates
CREATE TABLE `sp_images_cates` (  `ID` int(10) unsigned NOT NULL auto_increment,  `FAT_ID` int(10) unsigned NOT NULL,  `CATENAME` varchar(255) NOT NULL,  `CATENAME_EN` varchar(255) default NULL,  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
