DROP TABLE IF EXISTS sp_pages
CREATE TABLE `sp_pages` (  `ID` int(10) unsigned NOT NULL auto_increment,  `FAT_ID` int(10) unsigned NOT NULL,  `PAGE_NAME` varchar(255) NOT NULL,  `PAGE_TITLE` varchar(255) default NULL,  `PAGE_TITLE_EN` varchar(255) default NULL,  `PAGE_DESCRIPTION` text,  `PAGE_DESCRIPTION_EN` text,  `PAGE_KEYWORDS` text,  `PAGE_KEYWORDS_EN` text,  `PAGE_CONTENT` text,  `PAGE_CONTENT_EN` text,  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_products
CREATE TABLE `sp_products` (  `ID` int(10) unsigned NOT NULL auto_increment,  `CAT_ID` int(10) unsigned NOT NULL,  `PRO_NAME` varchar(255) NOT NULL,  `PRO_NAME_EN` varchar(255) default NULL,  `PRO_TYPE` varchar(255) default NULL,  `PRO_PRICE` decimal(8,2) default NULL,  `PRO_IMAGE` varchar(255) NOT NULL,  `PRO_CONTENT` text,  `PRO_CONTENT_EN` text,  `SUBMIT_DATE` datetime NOT NULL,  `READCOUNT` int(11) NOT NULL default '0',  `IS_SHOW` int(11) NOT NULL default '0',  `IS_HOMESHOW` int(11) NOT NULL default '0',  `IS_TOP` int(11) NOT NULL default '0',  `IS_DELETE` int(11) NOT NULL default '0',  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=49 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS sp_products_cates
CREATE TABLE `sp_products_cates` (  `ID` int(10) unsigned NOT NULL auto_increment,  `FAT_ID` int(10) unsigned NOT NULL,  `CATENAME` varchar(255) NOT NULL,  `CATENAME_EN` varchar(255) default NULL,  PRIMARY KEY  (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8
