DROP TABLE IF EXISTS ss_admuser
CREATE TABLE `ss_admuser` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `ADMNAME` varchar(100) NOT NULL,  `ADMPASS` char(50) NOT NULL,  `ADMPERMISSION` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_articles
CREATE TABLE `ss_articles` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `CAT_ID` int(10) unsigned NOT NULL,  `ART_TITLE` varchar(255) NOT NULL,  `ART_TITLE_EN` varchar(255) DEFAULT NULL,  `ART_CONTENT` text NOT NULL,  `ART_CONTENT_EN` text,  `AUTHOR` varchar(255) NOT NULL,  `AUTHOR_EN` varchar(255) DEFAULT NULL,  `SUBMIT_DATE` datetime NOT NULL,  `READCOUNT` int(11) NOT NULL DEFAULT '0',  `IS_SHOW` int(11) NOT NULL DEFAULT '0',  `IS_PUBLIC` int(11) NOT NULL DEFAULT '0',  `IS_HOMESHOW` int(11) NOT NULL DEFAULT '0',  `IS_TOP` int(11) NOT NULL DEFAULT '0',  `IS_DELETE` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_articles_cates
CREATE TABLE `ss_articles_cates` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `FAT_ID` int(10) unsigned NOT NULL,  `CATENAME` varchar(255) NOT NULL,  `CATENAME_EN` varchar(255) DEFAULT NULL,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_banner
CREATE TABLE `ss_banner` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `IMG_URL` varchar(255) NOT NULL,  `IMG_LINK` varchar(255) DEFAULT NULL,  `IMG_TITLE` varchar(255) DEFAULT NULL,  `IMG_DESC` varchar(255) DEFAULT NULL,  `ORDERNUM` int(10) unsigned NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_config
CREATE TABLE `ss_config` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `SITENAME` varchar(200) NOT NULL,  `SITENAME_EN` varchar(200) DEFAULT NULL,  `SITE_TITLE` varchar(200) NOT NULL,  `SITE_TITLE_EN` varchar(200) DEFAULT NULL,  `SITE_DESCRIPTION` text NOT NULL,  `SITE_DESCRIPTION_EN` text,  `SITE_KEYWORDS` text NOT NULL,  `SITE_KEYWORDS_EN` text,  `EMAIL` varchar(255) NOT NULL,  `PHONE` varchar(100) NOT NULL,  `FOX` varchar(100) DEFAULT NULL,  `QQ` varchar(100) DEFAULT NULL,  `MSN` varchar(100) DEFAULT NULL,  `ADDRESS` varchar(255) DEFAULT NULL,  `ADDRESS_EN` varchar(255) DEFAULT NULL,  `LINKMAN` varchar(100) DEFAULT NULL,  `LINKMAN_EN` varchar(100) DEFAULT NULL,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_guest_param
CREATE TABLE `ss_guest_param` (  `IS_MAIL` int(11) NOT NULL DEFAULT '1',  `IS_SHOW` int(11) NOT NULL DEFAULT '0',  `IS_VERICODE` int(11) NOT NULL DEFAULT '0',  `RECEIVE_EMAIL` varchar(255) DEFAULT NULL,  `REPLY_SMTP` varchar(255) DEFAULT NULL,  `REPLY_SMTP_USERNAME` varchar(255) DEFAULT NULL,  `REPLY_SMTP_PASSWORD` varchar(255) DEFAULT NULL) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_guestbook
CREATE TABLE `ss_guestbook` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `USER_NAME` varchar(255) NOT NULL,  `EMAIL` varchar(255) NOT NULL,  `QQMSN` varchar(255) DEFAULT NULL,  `PHONE` varchar(255) DEFAULT NULL,  `MOBILE` varchar(255) DEFAULT NULL,  `ADDRESS` varchar(255) DEFAULT NULL,  `COMPANY` varchar(255) DEFAULT NULL,  `CON_TITLE` varchar(255) DEFAULT NULL,  `CONTENT` text NOT NULL,  `CONTENT_REPLY` text,  `IS_MAIL` int(11) NOT NULL DEFAULT '1',  `SUBMIT_DATE` datetime NOT NULL,  `REPLY_DATE` datetime NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=MyISAM DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_images
CREATE TABLE `ss_images` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `CAT_ID` int(10) unsigned NOT NULL,  `IMG_NAME` varchar(255) NOT NULL,  `IMG_NAME_EN` varchar(255) DEFAULT NULL,  `IMG_URL` varchar(255) NOT NULL,  `IMG_DESC` text,  `IMG_DESC_EN` text,  `IS_SHOW` int(11) NOT NULL DEFAULT '0',  `IS_HOMESHOW` int(11) NOT NULL DEFAULT '0',  `IS_TOP` int(11) NOT NULL DEFAULT '0',  `IS_DELETE` int(11) NOT NULL DEFAULT '0',  `READCOUNT` int(11) NOT NULL DEFAULT '0',  `SUBMIT_DATE` datetime NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=16 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_images_cates
CREATE TABLE `ss_images_cates` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `FAT_ID` int(10) unsigned NOT NULL,  `CATENAME` varchar(255) NOT NULL,  `CATENAME_EN` varchar(255) DEFAULT NULL,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_link_group
CREATE TABLE `ss_link_group` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `GROUPNAME` varchar(255) NOT NULL,  `TEXT_OR_IMG` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_links
CREATE TABLE `ss_links` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `GROUP_ID` int(10) unsigned NOT NULL,  `LINK_NAME` varchar(255) NOT NULL,  `LINK_TEXT` varchar(255) NOT NULL,  `LINK_URL` varchar(255) NOT NULL,  `LINK_LOGO` varchar(255) NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_pages
CREATE TABLE `ss_pages` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `FAT_ID` int(10) unsigned NOT NULL,  `PAGE_NAME` varchar(255) NOT NULL,  `PAGE_TITLE` varchar(255) DEFAULT NULL,  `PAGE_TITLE_EN` varchar(255) DEFAULT NULL,  `PAGE_DESCRIPTION` text,  `PAGE_DESCRIPTION_EN` text,  `PAGE_KEYWORDS` text,  `PAGE_KEYWORDS_EN` text,  `PAGE_CONTENT` text,  `PAGE_CONTENT_EN` text,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_products
CREATE TABLE `ss_products` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `CAT_ID` int(10) unsigned NOT NULL,  `PRO_NAME` varchar(255) NOT NULL,  `PRO_NAME_EN` varchar(255) DEFAULT NULL,  `PRO_TYPE` varchar(255) DEFAULT NULL,  `PRO_PRICE` decimal(8,2) DEFAULT NULL,  `PRO_IMAGE` varchar(255) NOT NULL,  `PRO_CONTENT` text,  `PRO_CONTENT_EN` text,  `SUBMIT_DATE` datetime NOT NULL,  `READCOUNT` int(11) NOT NULL DEFAULT '0',  `IS_SHOW` int(11) NOT NULL DEFAULT '0',  `IS_HOMESHOW` int(11) NOT NULL DEFAULT '0',  `IS_TOP` int(11) NOT NULL DEFAULT '0',  `IS_DELETE` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=137 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_products_cates
CREATE TABLE `ss_products_cates` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `FAT_ID` int(10) unsigned NOT NULL,  `CATENAME` varchar(255) NOT NULL,  `CATENAME_EN` varchar(255) DEFAULT NULL,  `CATEDESC` text,  `CATEDESC_EN` text,  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=16 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS ss_qqkefu
CREATE TABLE `ss_qqkefu` (  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `IS_SHOW` int(11) NOT NULL DEFAULT '0',  `QQ_NUMS` varchar(255) DEFAULT NULL,  `QQ_POSITION` varchar(255) NOT NULL DEFAULT 'right',  `QQ_TOP` int(11) NOT NULL DEFAULT '160',  `QQ_COLOR` varchar(255) NOT NULL DEFAULT 'default_blue',  `QQ_STYLE` int(11) NOT NULL DEFAULT '1',  `QQ_EFFECT` int(10) unsigned NOT NULL DEFAULT '0',  `QQ_OPEN` int(10) unsigned NOT NULL DEFAULT '0',  `KF_TEL` varchar(255) NOT NULL DEFAULT '400-655-8888',  PRIMARY KEY (`ID`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
